custom_css:
/*作者：@kuku（小红书同名）  */

/*⚠️ 禁二传二改·禁商用*/

/* === 字体=== */
@font-face {
  font-family: "MiaoMiao";  
  src: url("https://files.catbox.moe/h6vwzr.ttf") format("truetype");
  font-display: swap;       
}

body, button, input, textarea {
  font-family: "MiaoMiao", "Microsoft YaHei", sans-serif !important;
}



/* 头像容器绝对定位 */
.mesAvatarWrapper {
    position: absolute;
    top: 9px; 
    width: 50px; 
    height: 50px;
    z-index: 3;
}

/* 用户头像位置 */
div[is_user="true"] .mesAvatarWrapper {
    right: -59px; 
    left: auto;
    transform: translateY(-10%); 
}

div[is_user="false"] .mesAvatarWrapper {
    left: -57px; 
    right: auto;
    transform: translateY(-10%);
}





/* 框长度 */
.mes_text {
    padding: 1px 1px 50px 1px !important; /* 上 右 下 左 */
}

/* 用户头像框 */
div[is_user="true"] .mesAvatarWrapper::before {
    content: "";
    position: absolute;
    width: var(--deco-size, 78px);
    height: var(--deco-size, 78px);
    background: url('https://files.catbox.moe/6p61t9.png') center/contain no-repeat;
    top: calc(var(--deco-size, 25px) * -0.6);
    left: 50%;
    transform: translateX(-50%);
    z-index: 3;
    pointer-events: none;
    animation: float 2.5s ease-in-out infinite;
}

/* AI头像框 */
div[is_user="false"] .mesAvatarWrapper::before {
    content: "";
    position: absolute;
    width: var(--deco-size, 95px);
    height: var(--deco-size, 95px);
    background: url('https://files.catbox.moe/fwj4ki.png') center/contain no-repeat;
    top: calc(var(--deco-size, 40px) * -0.6);
    left: 48%;
    transform: translateX(-50%);
    z-index: 3;
    pointer-events: none;

    animation: float 3.5s ease-in-out infinite;
}





/* 消息块上下间距 */
.mes {
    margin-top: 10px;
    margin-bottom: 15px;
}

/* 单独调整用户消息底部间距 */
div[is_user="true"] {
    margin-bottom: 20px; /* 增加底部留白 */
}


#chat {
    padding: 0 40px !important; 
}

div[is_user="true"] {
    margin-right: 15px; 
}

div[is_user="false"] {
    margin-left: 15px; 
}


/* 用户消息右对齐 */
div[is_user="true"] {
    margin-left: -30px; 
    margin-right: 20px; 
}

/* AI消息左对齐 */
div[is_user="false"] {
    margin-right: auto; 
    margin-left: 20px; 
}


/* 拟态气泡核心样式 */
.mes {
    position: relative;
    border-radius: 10px !important;
    box-shadow: 
        1px 1px 5px rgba(0, 0, 0, 0.08),
        1px 1px 5px rgba(255, 255, 255, 0.02),
        inset 2px 2px 4px rgba(255, 255, 255, 0.3),
        inset -2px -2px 4px rgba(0, 0, 0, 0.01);
    transition: all 0.3s ease;
    border: 0px solid rgba(255, 255, 255, 0.5) !important;

}


/* 用户消息气泡 */
div[is_user="true"] {
    background: linear-gradient(145deg, #F9F9F9 10%, #F5FFE4 70%, #DFFFF0 100%) !important;
    border-color: #ffb0c8 !important;
}

/* AI消息气泡 */
div[is_user="false"] {
    background: linear-gradient(145deg, #F9F9F9 10%, #F5FFE4 70%, #DFFFF0 100%) !important;
    border-color: #9dd9fc !important;
}



#chat {
  background-color: var(--chat-background-color) !important;
  backdrop-filter: none !important; 
}




/* 滑块图案 */
input[type="range"]::-webkit-slider-thumb {
    width: 55px;
    height: 55px; 
    background: url('https://files.catbox.moe/2m5xq2.png') no-repeat center center; 
    background-size: contain; 
    border: none;
    border-radius: 0; 
    box-shadow: none;
    transition: width 0.2s, height 0.2s;
    z-index: 2;
}




/* 层级覆盖保障 */
.avatar,
.avatar::before,
.avatar::after {
    z-index: auto !important; /* 重置头像层级 */
}


/* 消息楼层定位调整 */
.mesIDDisplay {
    position: absolute !important;
    top: 9px !important; /* 垂直位置：负数表示移到气泡外上方 */
    left: 200px !important; /* 水平偏移量 */
    display: inline-block !important;
    font-size: 0.7em !important;
    font-family: "LXGW 975 HazyGo SC 500W";
    padding: -10px -10px !important;
    z-index: 888 !important; /* 确保在装饰图之上 */
}



* 添加在全局样式中 */
#top-bar {

   z-index: 1000; /* 确保顶部栏层级 */
}

/* 顶部装饰图容器 */
#top-bar::after {
    content: "";
    position: fixed;
    top: var(--topBar-height,-30px); /* 匹配顶部栏高度 */
    left: 0;
    width: 100%;
    height: 105px;
    background: url('https://files.catbox.moe/wwhoe4.png') center/cover no-repeat;
    z-index: 999; /* 置于顶部栏下方 */
    pointer-events: none; /* 防止遮挡交互 */
    filter: opacity(0.) drop-shadow(0 2px 4px rgba(0,0,0,0.0));
}

/* 动态适配 */
@media (max-width: px) {
    #top-bar::after {
        height: 100px;
        background-size: auto 80%;
    }
}

/* 内容区下移 */
#main-content {
    margin-top: 150px; 
}


/* 修改引用文本样式 */
blockquote,
.quote-style,
[class*="quote"] {
    background-color: transparent !important;
    border: none !important;
    box-shadow: none !important;
    padding: 0 !important;
    margin: 0.8em 0 !important;
}

/* 移除所有伪元素装饰 */
blockquote::before,
blockquote::after {
    content: "" !important;
}


* ---预设--- */
input[type="text"],
input[type="number"],
textarea:not([type="search"]):focus {

}

.text_pole:not(.objective-task) {
  background-color: var(--ui-color-sec);
