    border: 1px solid 1px solid var(--SmartThemeBorderColor);
}

#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name, #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: var(--customThemeColor-purple);
}




/* 果冻风格下拉框 */
select {
    --jelly-color: #FFEAF0;   /* 主色 */
    --shadow-color: #FFEAF0; /* 投射色 */
    background: 
        linear-gradient(145deg, 
            rgba(245,255,228,1) 100%, 
            rgba(255,245,248,1) 90%) !important;
    border: px solid rgba(255,245,248,0.1) !important;
    border-radius: 10px !important;
    box-shadow:
        0 2px 20px -4px rgba(69,69,69,0.),
        inset 0 4px 4px rgba(255,255,255,0.2),
        inset 0 -2px 4px rgba(69,69,69,0.1) !important;
    transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94) !important;
    padding: px 36px 12px 16px !important;
    color: #454545 !important;
    backdrop-filter: blur(8px);
    position: relative;
}

/* 果冻材质层 */
select::after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border-radius: 10px;
    background: 
        linear-gradient(135deg, 
            rgba(255,255,255,0.6) 0%, 
            rgba(239,204,218,0.3) 100%);
    mix-blend-mode: overlay;
    pointer-events: none;
}




/* 聚焦状态微调 */
input[type="text"]:focus,
input[type="number"]:focus,
input[type="switch"]:focus,
input:not([type]):focus,
textarea:not([type="search"]):focus {
    border-color: #EFCCDA !important; /* 加深边框色 */
    box-shadow: 0 0 0 2px rgba(141, 222, 225, 0.3) !important;
}



/* 自定义勾选框配色 */
input[type="checkbox"] {
    --checkbox-bg: #FFEAF0;  /* 基础背景色 */
    --checkbox-accent: #FFEAF0; /* 强调色 */

    background-color: var(--checkbox-bg) !important;
    box-shadow: 0 0 0 px var(--checkbox-accent) !important;
    transition: all 0.2s ease;
}

/* 选中状态 */
input[type="checkbox"]:checked {
    background-color: var(--checkbox-accent) !important;
    box-shadow: 0 0 0 1px var(--checkbox-bg) !important;
}

/* 对勾符号 */
input[type="checkbox"]::before {
    box-shadow: inset 1em 1em var(--checkbox-bg) !important;
    filter: contrast(0.1);
}


/* 爱心图标 */
input[type="checkbox"]:checked::after {
    content: "";
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    font-size: 12px;
    line-height: 1;
    color: #454545;
    text-shadow: 0 2px 4px rgba(255,107,107,0.0);
}


/* 果冻立体风格 EFCCDA+454545 配色方案 */
:root {
  --jelly-bg: #F5FFE4;         /* 果冻基底色 */
  --jelly-accent: #FFEAF0;     /* 立体投射色 */
  --jelly-shadow: rgba(69, 69, 69, 0.011);
  --jelly-highlight: rgba(255, 255, 255, 0.9);
}

/* 果冻立体核心样式 */
.inline-drawer-header,
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4,
.standoutHeader {
  background: linear-gradient(
    145deg,
    var(--jelly-bg) 30%,
    color-mix(in srgb, var(--jelly-bg) 90%, white 10%)
  ) !important;
  border: px solid var(--jelly-accent) !important;
  box-shadow: 
    0 4px 18px var(--jelly-shadow),
    inset 2px 2px 4px var(--jelly-highlight),
    inset -2px -2px 4px color-mix(in srgb, var(--jelly-accent) 20%, transparent) !important;
  border-radius: 12px;
  backdrop-filter: blur(4px);
  transition: all 0.2s cubic-bezier(0.34, 1.56, 0.64, 1);
  position: relative;
  overflow: hidden;
}

/* 果冻材质伪元素 */
.standoutHeader::after,
.inline-drawer-header::after {
  content: "";
  position: absolute;
  top: -50%;
  left: -50%;
  width: 200%;
  height: 200%;
  background: radial-gradient(
    circle at 50% 50%,
    rgba(255, 255, 255, 0.4) 10%,
    transparent 60%
  );
  transform: rotate(15deg);
  pointer-events: none;
}



/* 立体文字样式 */
.standoutHeader strong,
.inline-drawer-header h4 {
  color: var(--jelly-accent) !important;
  text-shadow: 
    1px 1px 0 color-mix(in srgb, white 60%, var(--jelly-bg)),
    -1px -1px 1px var(--jelly-shadow);
  font-weight: 700;
  letter-spacing: 0.8px;
  position: relative;
  z-index: 2;
}




/* 极简渐变滚动条 */
::-webkit-scrollbar {
    width: 4px !important; 
    height: 5px !important; 
}

::-webkit-scrollbar-track {
    background: transparent !important; /* 完全透明轨道 */
}

::-webkit-scrollbar-thumb {
    background: linear-gradient(to bottom, 
        #FFFFFF 0%, 
        #FFFFFF 5%, 
        #597258 51%, 
        #FFFFFF 100%) !important;
    border-radius: 2px !important;
    border: none !important;
    transition: all 0.3s ease !important;
}

/* 输入框 */
input[type="text"],
input[type="number"],
input[type="switch"],
input:not([type]),
textarea:not([type="search"]) {
    background-color: #FFFFFF !important; 


}


/* 角色管理头像 */
#right-nav-panel .character_select .avatar {
    --avatar-base-size: 60px !important; 
    width: var(--avatar-base-size) !important;
    height: var(--avatar-base-size) !important;
    transform: scale(1) !important; 
    margin: 8px auto !important; 
}


#right-nav-panel .character_select .avatar img {
    width: 100% !important;
    height: 100% !important;
    object-fit: cover;
}


/* --- 滚动条 --- */
::-webkit-scrollbar-thumb:vertical {
    background-color: #FFEDF5;
    box-shadow: none;    
}



/* neo-range-input 背景美化 */
.neo-range-input {
    position: relative;
    background: linear-gradient(
            to right,
            rgba(255,255,255,0.2) 0%,
            rgba(255,255,255,0.1) 100%
        ),

    background-blend-mode: soft-light, multiply;
    box-shadow: inset 0 2px 8px rgba(0,0,0,0.2) !important;
}




